class Solution(object):
    def findTheDifference(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """
        letters = {}
        for c in s:
            letters[c] = letters.get(c,0) + 1
        for c in t:
            if c not in letters:
                return c
            else: 
                letters[c] = letters.get(c,0) - 1
        # refers to http://stackoverflow.com/questions/8023306/get-key-by-value-in-dictionary
        return list(letters.keys())[list(letters.values()).index(1)] if 1 in letters.values() else list(letters.keys())[list(letters.values()).index(-1)]

def solve_string(solution, s, t, expected):
        return "%10s %10s %8s %8s" % (s, t, expected, solution.findTheDifference(s,t))

s = Solution()
print("%10s %10s %8s %8s" % ("s", "t", "Expected", "Result"))
""
print(solve_string(s,"abcd","abcde","e"))
print(solve_string(s,"",
"","t"))
